mapper.select:
    select 
   a.id, b.id as news_id, a.news_launch_config, a.status, a.aduit_user_id, a.aduit_des, a.aduit_date, a.create_user_id, 
    a.create_date,b.news_title
    from news_launch a left join news b on a.news_id = b.id 
    where a.isDel = 0 and a.status in (0,2)

       		 and a.create_date >= #{record.startDate,jdbcType=TIMESTAMP}  


       		 and a.create_date <= #{record.endDate,jdbcType=TIMESTAMP}  



	       and b.news_title like concat('%', #{record.newsTitle}, '%')

	  order by a.id desc

	        LIMIT #{page.pageLimit},#{page.pageSize}

    select count(1)
    from news_launch a left join news b on a.news_id = b.id 
    where a.isDel = 0  and a.status in (0,2)

       		 and a.create_date >= #{startDate,jdbcType=TIMESTAMP}  


       		 and a.create_date <= #{endDate,jdbcType=TIMESTAMP}  



	       and b.news_title like concat('%', #{newsTitle}, '%')
