mapper.select:
    select a.id, a.members_id,a.createDate, a.content, a.news_publish_id, c.news_title
    from members_comments_news a, news_publish b,news c  where a.isDel = 0 and a.news_publish_id = b.id and b.news_id = c.id 


	       and a.members_id = #{record.membersId,jdbcType=INTEGER}

	     order by a.id desc

	        LIMIT #{page.pageLimit},#{page.pageSize}

  	  select count(1)
    from members_comments_news a, news_publish b,news c  where a.isDel = 0 and a.news_publish_id = b.id and b.news_id = c.id 
   	and c.isDel =0


	       and a.members_id = #{membersId,jdbcType=INTEGER}



	       and b.website_id = #{websiteId,jdbcType=INTEGER}
